#
# [1508] Range Sum of Sorted Subarray Sums
#
from typing import List


# @lc code=start
class Solution:
    def rangeSum(self, nums: List[int], n: int, left: int, right: int) -> int:
        nums = nums[:n]
        n_nums: List[int] = []
        # n_n: int = 0
        for i in range(len(nums)):
            n_nums.append(nums[i])
            # n_n += 1
            # if n_n >= n * (n + 1) / 2:
            #     break

            for j in range(i + 1, len(nums)):
                n_nums.append(n_nums[-1] + nums[j])
                # n_n += 1
                # if n_n >= n * (n + 1) / 2:
                #     break

            # if n_n >= n * (n + 1) / 2:
            #     break

        # print(n_nums)
        # print(len(n_nums))

        return sum(sorted(n_nums)[left - 1 : right]) % 1000000007


